import React from 'react'
import ReactDOM from 'react-dom'

const Part= (props) =>{
  return(
    <div>
      <p>{props.part} {props.exercise}</p>
    </div>
  )
}

const Header = (props) =>{
  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) =>{
  return(
    <div>
      <Part part={props.part1} exercise={props.part1.exercises}/>
      <Part part={props.part2} exercise={props.part2.exercises}/>
      <Part part={props.part3} exercise={props.part3.exercises}/>
    </div>
  )
}

const Total = (props) =>{
  return(
    <div>
      <p>The total number of exercises is: {props.exercises1 + props.exercises2 + props.exercises3}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = 
  [{
    name: 'Fundamentals of React',
    exercises: 10
  }
  ,{
    name: 'Using props to pass data',
    exercises: 7
  }
  ,{
    name: 'State of a component',
    exercises: 14
  }
  ]
  return (
    <div>
      <Header course={course} />
      <Content part1={parts[0].name} exercises1={parts[0].exercises} part2={parts[1].name} exercises2={parts[1].exercises} part3={parts[2].name} exercises3={parts[2].exercises} />
      <Total exercises1={parts[0].exercises} exercises2={parts[1].exercises} exercises3={parts[2].exercises} />
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
